definition.script:
pipeline {
    agent none
    stages {
        stage ('build') {
            agent {label 'slaveone'}
            steps {
                 sh 'pwd'
                 sh 'git clone https://github.com/Amruthateco/automation-scripts.git'
                 sh 'sh /home/slave/workspace/firstpipeline/automation-scripts/maven.sh'
                 sh 'scp -r /home/slave/workspace/firstpipeline/hello-world-war/target/hello-world-war-1.0.0.war root@172.31.9.64:/opt/tomcat/webapps'
            }
        }
stage ('deploy') {
            agent {label 'slavetwo'}
                steps{
                   sh 'sudo sh /opt/tomcat/bin/startup.sh'
                }
            }
        }
    }
